mapper.select:
select 

from SCORE
where STUDENT_ID = #{studentId,jdbcType=DECIMAL}
  and COURSE_ID = #{courseId,jdbcType=DECIMAL}

mapper.delete:
delete from SCORE
where STUDENT_ID = #{studentId,jdbcType=DECIMAL}
  and COURSE_ID = #{courseId,jdbcType=DECIMAL}

mapper.update:
update SCORE


    SCORE = #{score,jdbcType=DECIMAL},


    TEACHER_ID = #{teacherId,jdbcType=DECIMAL},


where STUDENT_ID = #{studentId,jdbcType=DECIMAL}
  and COURSE_ID = #{courseId,jdbcType=DECIMAL}

update SCORE
set SCORE = #{score,jdbcType=DECIMAL},
  TEACHER_ID = #{teacherId,jdbcType=DECIMAL}
where STUDENT_ID = #{studentId,jdbcType=DECIMAL}
  and COURSE_ID = #{courseId,jdbcType=DECIMAL}
